mapper.select:
select h_id, h_name, h_add1, h_add2, h_add3, h_price
from hotel
where h_id = #{h_id}

mapper.update:
update booking
set b_chkInDate = #{b_chkInDate}, b_chkOutDate = #{b_chkOutDate}, b_price = #{b_price}, b_humanNum = #{b_humanNum}, b_petNum = #{b_petNum}, b_name = #{b_name}
where b_id = #{b_id}

mapper.select:
select m_nick
from member
where m_id = #{m_id}

mapper.update:
update booking
set b_state = '예약완료'
where b_id = #{b_id}
